    // DEVOLVER
    private void buttonMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_buttonMousePressed
        //jLabel1.setText(String.valueOf(arreglo.tama√±o_arreglo()));
        String valor =  txtFrase.getText();
        String pala = null;
        int lon = valor.length();
        int i=0, cont= 0, result = 0;
        
        result = palabra(valor,lon,i,cont,pala);
        
        if(!(valor.equals("")))
            txtEspacios.setText("La Oracion tinen "+String.valueOf(result)+ " espacios en blanco");
    }//GEN-LAST:event_buttonMousePressed

